import { Component, OnInit } from '@angular/core';
import { ApiService } from './api-service.service';

@Component({
  selector: 'app-data', 
  template: `
    <h2>Data from API</h2>
    <ul>
      <li *ngFor="let item of apiData">{{ item.name }}</li>
    </ul>
  `
})
export class DataComponent implements OnInit {
  apiData?: any[]; // This will hold the data fetched from the API

  constructor(private apiService: ApiService) {}

  ngOnInit(): void {
    // When the component initializes, make an HTTP GET request to fetch data from the API
    this.apiService.getSomeData().subscribe(
      (data) => {
        this.apiData = data; // Store the fetched data in the apiData property
      },
      (error) => {
        console.error('Error fetching data from API:', error);
      }
    );
  }
}
